    def write_number(self, number):
        symbol = self.label_result.text()
        if symbol == '0' or self.is_equal:
            self.is_equal = False
            symbol = ''
        all_text = symbol + number
        self.label_result.setText(all_text)
        # print(number)

    def results(self):
        res = eval(self.label_result.text())
        print(type(res))
        self.label_result.clear()
        self.write_number(str(res))
        self.is_equal = True

